'use strict';

var api = require('./api/v1');
var apiGen = require('./apigen');
var processArgs = require('./process-args');

var RsnApi = function RsnApi(config) {
  return apiGen('v1', api, config);
};

Object.assign(RsnApi, {
  processArgs: processArgs,
  api: api,

  /** @deprecated */
  Testnet: function Testnet(config) {
    console.error('deprecated, change RsnApi.Testnet(..) to just RsnApi(..)');
    return RsnApi(config);
  },

  /** @deprecated */
  Localnet: function Localnet(config) {
    console.error('deprecated, change RsnApi.Localnet(..) to just RsnApi(..)');
    return RsnApi(config);
  }
});

module.exports = RsnApi;